import { useLocation } from "react-router-dom";

const Dashboard = (props) => {
  const location = useLocation(); // Get the passed state

  // State to track the active tab
  const [activeTab, setActiveTab] = useState(location.state?.tab || "1"); // Default to "1" if no state is passed

  const allTournaments = useSelector(selectAllTournaments);
  const currentTournaments = useSelector(selectCurrentTournaments);
  const pastTournaments = useSelector(selectPastTournaments);
  const upcomingTournaments = useSelector(selectUpcomingTournaments);

  // Function to toggle the active tab
  const toggleTab = (tab) => {
    if (activeTab !== tab) {
      setActiveTab(tab);
    }
  };

  return (
    <div id="dashboard" className="ts-alignment ts-dashboard">
      <Nav tabs className="border-0">
        <NavItem>
          <NavLink
            className={`${activeTab === "1" ? "active" : ""} `}
            onClick={() => toggleTab("1")}
          >
            Current
          </NavLink>
        </NavItem>
        <NavItem>
          <NavLink
            className={activeTab === "2" ? "active" : ""}
            onClick={() => toggleTab("2")}
          >
            Upcoming
          </NavLink>
        </NavItem>
        <NavItem>
          <NavLink
            className={activeTab === "3" ? "active" : ""}
            onClick={() => toggleTab("3")}
          >
            Past
          </NavLink>
        </NavItem>
      </Nav>
      <TabContent activeTab={activeTab} className="mt-5">
        <TabPane tabId="1">
          <Row>
            {currentTournaments.map((item, idx) => (
              item && <TournamentCard item={item} tournamentDate="current" key={`current-tournament${idx}`} />
            ))}
          </Row>
        </TabPane>
        <TabPane tabId="2">
          <Row>
            {upcomingTournaments.map((item, idx) => (
              item && <TournamentCard item={item} tournamentDate="Upcoming" key={`current-tournament${idx}`} />
            ))}
          </Row>
        </TabPane>
        <TabPane tabId="3">
          <Row>
            {pastTournaments.map((item, idx) => (
              item && <TournamentCard item={item} tournamentDate="past" key={`current-tournament${idx}`} />
            ))}
          </Row>
        </TabPane>
      </TabContent>
    </div>
  );
};

export default Dashboard;
