import {useState, useEffect} from 'react';
import {
    Col,
    Card,
    CardBody,
    CardTitle,
    CardText,
    Button,
  } from 'reactstrap';

  import {useSpring, animated} from 'react-spring';

const TournamentCard = ({ item }) => {
    console.log('%csrc/components/card/TournamentCard.js:14 item', 'color: #007acc;', item);
    const { name, date_utc, start_time, tournament_type, playoff_elimination_type} = item;
    const [toggle, setToggle] = useState(false);
    console.log('%csrc/components/card/TournamentCard.js:17 name', 'color: #007acc;', name);

    const animatedStyle = useSpring({
        opacity: toggle ? 1 : 0,
        transform: toggle ? 'scale(1,1)' : 'scale(1,0)',
        config: { duration: 500 }
    });

    useEffect(() => {
        setToggle(true);
    }, []);

    return (
        <Col sm="6">
            <animated.div style={animatedStyle}>
                <Card className="bg-transparent border-1 border-white mb-5">
                    <CardBody>
                    <i class="fa-solid fa-calendar-days"></i>
                        <div className="border-b-1 border-white d-flex align-items-center justify-content-between">
                            <CardTitle className="fw-bold card-title">{name}</CardTitle>
                            <div className="d-flex">
                                <span className="calendar-icon">
                                    <svg fill="#fff" xmlns="http://www.w3.org/2000/svg" 
                                        viewBox="0 0 448 512"
                                        >{/*!Font Awesome Free 6.7.2 by @fontawesome - https://fontawesome.com License - https://fontawesome.com/license/free Copyright 2025 Fonticons, Inc.-->*/}
                                        <path d="M128 0c17.7 0 32 14.3 32 32l0 32 128 0 0-32c0-17.7 14.3-32 32-32s32 14.3 32 32l0 32 48 0c26.5 0 48 21.5 48 48l0 48L0 160l0-48C0 85.5 21.5 64 48 64l48 0 0-32c0-17.7 14.3-32 32-32zM0 192l448 0 0 272c0 26.5-21.5 48-48 48L48 512c-26.5 0-48-21.5-48-48L0 192zm64 80l0 32c0 8.8 7.2 16 16 16l32 0c8.8 0 16-7.2 16-16l0-32c0-8.8-7.2-16-16-16l-32 0c-8.8 0-16 7.2-16 16zm128 0l0 32c0 8.8 7.2 16 16 16l32 0c8.8 0 16-7.2 16-16l0-32c0-8.8-7.2-16-16-16l-32 0c-8.8 0-16 7.2-16 16zm144-16c-8.8 0-16 7.2-16 16l0 32c0 8.8 7.2 16 16 16l32 0c8.8 0 16-7.2 16-16l0-32c0-8.8-7.2-16-16-16l-32 0zM64 400l0 32c0 8.8 7.2 16 16 16l32 0c8.8 0 16-7.2 16-16l0-32c0-8.8-7.2-16-16-16l-32 0c-8.8 0-16 7.2-16 16zm144-16c-8.8 0-16 7.2-16 16l0 32c0 8.8 7.2 16 16 16l32 0c8.8 0 16-7.2 16-16l0-32c0-8.8-7.2-16-16-16l-32 0zm112 16l0 32c0 8.8 7.2 16 16 16l32 0c8.8 0 16-7.2 16-16l0-32c0-8.8-7.2-16-16-16l-32 0c-8.8 0-16 7.2-16 16z"/>
                                    </svg>
                                </span> 
                                <span>Mar 11th 2025</span></div>
                        </div>

                        <CardText>
                        With supporting text below as a natural lead-in to additional
                        content.
                        </CardText>
                        <Button>Go somewhere</Button>
                    </CardBody>
                </Card>
            </animated.div>
        </Col>

    )


}

export default TournamentCard;
