import {useState, useEffect} from 'react';
import {
    Col,
    Card,
    CardBody,
    CardTitle,
    CardText,
    Button,
  } from 'reactstrap';

  import {useSpring, animated} from 'react-spring';

const TournamentCard = ({ item, tournamentDate }) => {
    const { name, date_utc, start_time, tournament_type, playoff_elimination_type, location} = item;
    const [toggle, setToggle] = useState(false);
    console.log('%csrc/components/card/TournamentCard.js:16 tournamentDate', 'color: #007acc;', tournamentDate);

    const animatedStyle = useSpring({
        opacity: toggle ? 1 : 0,
        transform: toggle ? 'scale(1,1)' : 'scale(1,0)',
        config: { duration: 500 }
    });

    useEffect(() => {
        setToggle(true);
    }, []);

    return (
        <Col sm="6">
            <animated.div style={animatedStyle}>
                <Card className="bg-transparent mb-5">
                    <CurrentTournamentCardBody item={item}/>
                </Card>
            </animated.div>
        </Col>

    )


}

export default TournamentCard;
