import { useEffect, useRef } from 'react';
import { Formik, Field, Form, ErrorMessage } from 'formik';
import { FormGroup, Label, Button } from 'reactstrap';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { validateCreateTournamentForms } from '../utils/tournaments/validateCreateTournamentForm';

// ✅ Google API Key (Use ENV Variables for security)
const GOOGLE_API_KEY = "YOUR_GOOGLE_API_KEY"; // Replace with actual API Key

const CreateATournament = () => {
    const searchBoxRef = useRef(null); // Ref for Google Places SearchBox
    const searchBoxInstance = useRef(null); // Ref for Google SearchBox Instance

    useEffect(() => {
        const loadGoogleMapsScript = () => {
            if (window.google) {
                initializeSearchBox();
                return;
            }
            const script = document.createElement("script");
            script.src = `https://maps.googleapis.com/maps/api/js?key=${GOOGLE_API_KEY}&libraries=places`;
            script.async = true;
            script.defer = true;
            script.onload = initializeSearchBox;
            document.body.appendChild(script);
        };

        const initializeSearchBox = () => {
            if (!searchBoxRef.current || searchBoxInstance.current) return;

            searchBoxInstance.current = new window.google.maps.places.SearchBox(searchBoxRef.current);
        };

        loadGoogleMapsScript();
    }, []);

    return (
        <div className="ts-form">
            <h1 className="mb-5">Create A Tournament</h1>
            <Formik
                initialValues={{
                    name: '',
                    date: new Date(),
                    time: '',
                    address: '',
                    city: '',
                    state: '',
                    zip: '',
                    tournamentType: '',
                    playoffType: ''
                }}
                onSubmit={(values, { resetForm }) => {
                    console.log('Form values:', values);
                    resetForm();
                }}
                validate={validateCreateTournamentForms}
            >
                {({ values, setFieldValue }) => {
                    useEffect(() => {
                        if (!searchBoxInstance.current) return;

                        searchBoxInstance.current.addListener("places_changed", () => {
                            const places = searchBoxInstance.current.getPlaces();
                            if (!places.length) return;

                            const place = places[0];
                            const addressComponents = place.address_components;
                            const formattedAddress = place.formatted_address;
                            let city = '';
                            let state = '';
                            let zip = '';

                            addressComponents.forEach(component => {
                                if (component.types.includes('locality')) {
                                    city = component.long_name;
                                }
                                if (component.types.includes('administrative_area_level_1')) {
                                    state = component.short_name;
                                }
                                if (component.types.includes('postal_code')) {
                                    zip = component.long_name;
                                }
                            });

                            // ✅ Update Formik fields
                            setFieldValue("address", formattedAddress);
                            setFieldValue("city", city);
                            setFieldValue("state", state);
                            setFieldValue("zip", zip);
                        });
                    }, [setFieldValue]);

                    return (
                        <Form>
                            {/* Tournament Name */}
                            <FormGroup>
                                <Label htmlFor='name'>Tournament Name</Label>
                                <Field name="name" className='form-control bg-transparent text-white' />
                                <ErrorMessage name="name">{(msg) => <p className="text-danger">{msg}</p>}</ErrorMessage>
                            </FormGroup>

                            {/* Date Picker */}
                            <FormGroup>
                                <Label htmlFor='date'>Date</Label>
                                <DatePicker
                                    showIcon
                                    selected={values.date}
                                    onChange={(date) => setFieldValue("date", date)}
                                    className="form-control bg-transparent text-white"
                                />
                                <ErrorMessage name="date">{(msg) => <p className="text-danger">{msg}</p>}</ErrorMessage>
                            </FormGroup>

                            {/* Time Selection */}
                            <FormGroup>
                                <Label htmlFor='time'>Start Time</Label>
                                <Field name="time" as="select" className='form-control bg-transparent text-white'>
                                    <option value="">Select Time</option>
                                    <option value="10:00 AM">10:00 AM</option>
                                    <option value="12:00 PM">12:00 PM</option>
                                    <option value="3:00 PM">3:00 PM</option>
                                </Field>
                                <ErrorMessage name="time">{(msg) => <p className="text-danger">{msg}</p>}</ErrorMessage>
                            </FormGroup>

                            {/* Google Address SearchBox */}
                            <FormGroup>
                                <Label htmlFor='address'>Address</Label>
                                <input
                                    ref={searchBoxRef}
                                    type="text"
                                    className="form-control bg-transparent text-white"
                                    placeholder="Enter address..."
                                />
                                <Field type="hidden" name="address" />
                                <ErrorMessage name="address">{(msg) => <p className="text-danger">{msg}</p>}</ErrorMessage>
                            </FormGroup>

                            {/* Tournament Type */}
                            <FormGroup>
                                <Label htmlFor='tournamentType'>Tournament Type</Label>
                                <Field name="tournamentType" className='form-control bg-transparent text-white' />
                                <ErrorMessage name="tournamentType">{(msg) => <p className="text-danger">{msg}</p>}</ErrorMessage>
                            </FormGroup>

                            {/* Playoff Type */}
                            <FormGroup>
                                <Label htmlFor='playoffType'>Playoff Type</Label>
                                <Field name="playoffType" as="select" className='form-control bg-transparent text-white'>
                                    <option value="">Playoff Type</option>
                                    <option value="Single">Single Elimination</option>
                                    <option value="Double">Double Elimination</option>
                                </Field>
                                <ErrorMessage name="playoffType">{(msg) => <p className="text-danger">{msg}</p>}</ErrorMessage>
                            </FormGroup>

                            {/* Submit Button */}
                            <FormGroup className="mt-3 float-end" color="primary">
                                <Button type='submit' color='primary'>Create Tournament</Button>
                            </FormGroup>
                            <div className="clearfix"></div>
                            <p className="mt-4">Don't have an account? <a href="/sign-up">Sign up here</a></p>
                        </Form>
                    );
                }}
            </Formik>
        </div>
    );
};

export default CreateATournament;
