import {useState, useEffect} from 'react';
import {
    Col,
    Card,
    CardBody,
    CardTitle,
    CardText,
    Button,
  } from 'reactstrap';

  import {useSpring, animated} from 'react-spring';

const TournamentCard = ({ item }) => {
    console.log('%csrc/components/card/TournamentCard.js:14 item', 'color: #007acc;', item);
    const { name, date_utc, start_time, tournament_type, playoff_elimination_type} = item;
    const [toggle, setToggle] = useState(false);
    console.log('%csrc/components/card/TournamentCard.js:17 name', 'color: #007acc;', name);

    const animatedStyle = useSpring({
        opacity: toggle ? 1 : 0,
        transform: toggle ? 'scale(1,1)' : 'scale(1,0)',
        config: { duration: 500 }
    });

    useEffect(() => {
        setToggle(true);
    }, []);

    return (
        <Col sm="6">
            <animated.div style={animatedStyle}>
                <Card className="bg-transparent border-1 border-white mb-5">
                    <CardBody>
                    <i class="fa-solid fa-calendar-days"></i>
                        <div className="border-b-1 border-white justify-content-between mb-4">
                            <div className="d-flex">

                                <span>Mar 11th 2025</span>
                            </div>
                            <CardTitle className="fw-bold card-title">{name}</CardTitle>
                        </div>

                        <CardText>
                        With supporting text below as a natural lead-in to additional
                        content.
                        </CardText>
                        <Button>Go somewhere</Button>
                    </CardBody>
                </Card>
            </animated.div>
        </Col>

    )


}

export default TournamentCard;
